	public String encode(int[][][] level) {
		StringBuilder string = new StringBuilder();
		for(int x=0;x<10;x++) {
			for(int y=0;y<10;y++) {
				for(int z=0;z<10;z++) {
					if(x==9 && y==9 && z==9) {
						string.append(level[x][y][z]);
					} else {
						string.append(level[x][y][z]+",");
					}
				}
			}
		}
		return string.toString();
	}
	
